mapper.select:
select q_num, p_id, co_id, qna_title, q_passwd, q_regdate, qref_number, context, readCount, r from 
(select q_num, p_id, co_id, qna_title, q_passwd, q_regdate, qref_number, context, readCount, ROWNUM r from qna_board order by qref_number DESC) 
     
where r>=#{start} 
 
 
 and r<=#{end}
